var temp = `
  <h1>Hello World</h1>
  <h2>Hello again</h2>
  <p>
    Hello there everyone, this is a pretty cool thing <small>hello</small>{{ hello }}.
    <a href="hello">This is some cool stuff</a>
  </p>
  <ul>
    <li each="user in users">{{ user.name }}</li>
  </ul>

  <p stream="user">{{ user }}</p>

`

let temp = `
  <p>
    Hello there everyone, this is a pretty cool thing <small>hello</small>{{ hello if user }}.
    <a href="hello">This is some cool stuff</a>
  </p>

  <ul>
    <li each="user in users">{{ user.name if user.nothing }}</li>
    <li>Hello there</li>
  </ul>
`

var users = [
  {name: 'Colin'},
  {name: 'Colin'},
  {name: 'Colin'},
  {name: 'Colin'},
  {name: 'Colin'}
]
var greet = 'hello'

var t = new Template(temp)
t.prop = function(arg) {
  if (arg == 'users') {
    return users
  } else {
    return greet
  }
}


console.log(t.render())
users[0].name = 'Colin the greatest'
greet = 'something else'

t.updateAll()

console.log(t.render())